    <ul>
      <li parentName="ul">{`Sub-folder: assets`}</li>
      <li parentName="ul">{`Sub-folder: sketch`}</li>
      <li parentName="ul">{`Sub-folder: versions`}</li>
      <li parentName="ul">{`File: style-name.md`}</li>
