    <ul>
      <li parentName="ul">{`Sub-folder: assets`}</li>
      <li parentName="ul">{`Sub-folder: sketch`}</li>
      <li parentName="ul">{`Sub-folder: versions`}<ul parentName="li">
          <li parentName="ul">{`File: component-name.md`}</li>
