export const rightToc = [{
  value: 'Why responsive Design?',
  id: 'why-responsive-design',
  children: [{
    value: 'Benefits',
    id: 'benefits',
    children: []
  }]
}, {
  value: 'Creating visual concepts',
  id: 'creating-visual-concepts',
  children: []
}, {
  value: 'Breakpoints',
  id: 'breakpoints',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "why-responsive-design"
    }}>{`Why responsive Design?`}</h2>
    <p>{`Vodafone webpages behave responsive in all viewports below 1025px. This means that all elements and components are visible and usable on the user's device (mobile, phablet, tablet, desktop). `}</p>
    <h3 {...{
      "id": "benefits"
    }}>{`Benefits`}</h3>
    <ul>
      <li parentName="ul">{`High consistency in user experience for Vodafone customers across all devices and screensizes at all times.`}</li>
      <li parentName="ul">{`Responsiveness supports editorial work because only one source needs to be maintained for several screen views with the result of consistency and cost efficiency.`}</li>
      <li parentName="ul">{`Responsive behaviour is a key requirement for search engine optimization.`}</li>
    </ul>
    <p>{`The following principles define the responsive framework for all Vodafone webpages and provide guidance for everyone building webpages. These principles focus on our key-requirements: consistency, reusability, compatibility.`}</p>
    <p><img parentName="p" {...{
        "src": "/vf-design-system-v2/img/responsive-design.svg",
        "alt": "responsive design"
      }}></img></p>
    <h2 {...{
      "id": "creating-visual-concepts"
    }}>{`Creating visual concepts`}</h2>
    <p>{`These viewport-widths are our recommended screensizes for building layouts with Sketch:`}</p>
    <ul>
      <li parentName="ul">{`Mobile: 320px`}</li>
      <li parentName="ul">{`Tablet: 768px`}</li>
      <li parentName="ul">{`Desktop: 1280px`}</li>
    </ul>
    <p><strong parentName="p">{`Important:`}</strong>{` During the design process for new components/elements it is essential to ensure always that also "critical" views are considered. Therefore the designer is advised to provide a DRAFT version the following viewports:`}</p>
    <ul>
      <li parentName="ul">{`Mobile: approx. 580px`}</li>
      <li parentName="ul">{`Tablet: approx. 620px`}</li>
    </ul>
    <p><img parentName="p" {...{
        "src": "/vf-design-system-v2/img/design-templates.svg",
        "alt": "design templates"
      }}></img></p>
    <h2 {...{
      "id": "breakpoints"
    }}>{`Breakpoints`}</h2>
    <p>{`The Vodafone framework works with the following breakpoints: `}</p>
    <p>{`MINIMUM supported viewport: `}</p>
    <p>{`320px Mobile`}</p>
    <p>{`320-599px----------------------------------------------------Breakpoint @600px`}</p>
    <p>{`Tablet`}</p>
    <p>{`600-1024px--------------------------------------------------Breakpoint @1024px`}</p>
    <p>{`Desktop`}</p>
    <blockquote>
      <p parentName="blockquote">{`1024px MAXIMUM supported viewport:`}</p>
    </blockquote>
    <p>{`1025px `}</p>
    <p>{`Larger viewports are filled with white space on the left and right sides with no further content-adaption. Benefits:
Breakpoints are essential to provide an ideal user experience on all screens (mobile, phablet, tablet, desktop).`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
